 */
class LUM_User extends BaseLUM_User
{
    /**
     * Returns user's profile URI.
     *
     * @return string
     */
    public function getProfileUri($prefix = '')
    {
        return sprintf('%saccount/%d/%s', $prefix, $this->userid, $this->getSlug());
    }

    /**
     * Generates slug from user's name.
     *
     * @return slug
     *
     * @see http://www.intrepidstudios.com/blog/2009/2/10/function-to-generate-a-url-friendly-string.aspx
     */
    public function getSlug()
    {
        // Build slug
        $result = strtolower($this->name);

        $result = preg_replace("/[^a-z0-9\s-]/", "", $result);
        $result = trim(preg_replace("/[\s-]+/", " ", $result));
        $result = preg_replace("/\s/", "-", $result);

        return $result;
    }
}
